    public function testGetList()
    {
        global $g_ado_db;

        // builds basic parameters
        $leftOperand = 'IdPublication';
        $rightOperand = 1;
        $operator = new Operator('is', 'sql');
        $comparisonOperationObj1 = new ComparisonOperation($leftOperand,
                                                           $operator,
                                                           $rightOperand);
        $leftOperand = 'IdLanguage';
        $rightOperand = 1;
        $operator = new Operator('is', 'sql');
        $comparisonOperationObj2 = new ComparisonOperation($leftOperand,
                                                           $operator,
                                                           $rightOperand);

        // builds the constraint
        $leftOperand = 'PublicationDate';
        $rightOperand = '2007-08-12';
        $operator = new Operator('smaller', 'sql');
        $comparisonOperationObj3 = new ComparisonOperation($leftOperand,
                                                           $operator,
                                                           $rightOperand);

        // sets the params for Issue::GetList()
        $params = array($comparisonOperationObj1,
                        $comparisonOperationObj2,
                        $comparisonOperationObj3);
        $order = array('Name' => 'DESC');
        $limitStart = 0;
        $limitOffset = 0;

        // gets the list of issues
        $issuesList = Issue::GetList($params, $order, $limitStart, $limitOffset);

        $iList = array();
        $sql = "SELECT * FROM Issues WHERE PublicationDate < '2007-08-12' AND Published = 'Y' ORDER BY Name DESC";
        $issues = $g_ado_db->GetAll($sql);
        foreach ($issues as $issue) {
            $iList[] = new Issue($issue['IdPublication'],
                                 $issue['IdLanguage'],
                                 $issue['Number']);
        }

        $this->assertEquals(sizeof($iList), sizeof($issuesList));

        $i = 0;
        $matches = 0;
        foreach ($issuesList as $issue) {
            $this->assertEquals($iList[$i]->getIssueNumber(), $issue->getIssueNumber());
            $i++;
        }
    } // fn testGetList

