        // sets the params for Issue::GetList()
        $params = array($comparisonOperationObj);
        $order = array('Name' => 'DESC');
        $limitStart = 0;
        $limitOffset = 0;

        $this->assertEquals(null, Issue::GetList($params, $order, $limitStart, $limitOffset));
    } // fn testGetListBadParameter

