    public function testGetList()
    {
        global $g_ado_db;

        // builds the constraint
        $leftOperand = 'ArticleAttachments.fk_article_number';
        $rightOperand = '39';
        $operator = new Operator('is', 'sql');
        $comparisonOperationObj = new ComparisonOperation($leftOperand,
                                                          $operator,
                                                          $rightOperand);

        // sets the params for ArticleAttachment::GetList()
        $params = array($comparisonOperationObj);
        $order = array();
        $limitStart = 0;
        $limitOffset = 0;

        // gets the list of article attachments
        $attachmentsList = ArticleAttachment::GetList($params, $order, $limitStart, $limitOffset);

        $aList = array();
        $sql = "SELECT * FROM ArticleAttachments WHERE fk_article_number = 39";
        $attachments = $g_ado_db->GetAll($sql);
        foreach ($attachments as $attachment) {
            $aList[] = new ArticleAttachment($attachment['fk_article_number'],
                                             $attachment['fk_attachment_id']);
        }

        $this->assertEquals(sizeof($aList), sizeof($attachmentsList));

        $i = 0;
        foreach ($attachmentsList as $attachment) {
            $this->assertEquals($aList[$i]->getAttachmentId(), $attachment->getAttachmentId());
            $i++;
        }
    } // fn testGetList

    public function testGetListBadParameter()
    {
        global $g_ado_db;

        // builds the constraint
        $leftOperand = 'Attachment.file_name';
        $rightOperand = 'Robotic submarine reaches new depths';
        $operator = new Operator('not', 'sql');
