        // sets the params for ArticleAttachment::GetList()
        $params = array($comparisonOperationObj);
        $order = array();
        $limitStart = 0;
        $limitOffset = 0;

        $this->assertEquals(null, ArticleAttachment::GetList($params, $order, $limitStart, $limitOffset));
    } // fn testGetListBadParameter

