    }

    public function testBuildURI()
    {
    	$this->assertEquals('/en/camp/campsite_news/704/', $this->m_uri1->buildURI());
        $this->assertEquals('/en/camp/campsite_news/', $this->m_uri2->buildURI());
        $this->assertEquals('/', $this->m_uri3->buildURI());
